mapper.insert:

    SELECT IFNULL(MAX(id), 0) + 1 FROM User_Savings

INSERT INTO User_Savings (
id, user_id, product_id, principal_amount, start_date, maturity_date, monthly_payment
)
VALUES (
#{id}, #{userId}, #{productId}, #{principalAmount}, #{startDate}, #{maturityDate}, #{monthlyPayment}
)
ON DUPLICATE KEY UPDATE
principal_amount = #{principalAmount},
start_date = #{startDate},
maturity_date = #{maturityDate},
monthly_payment = #{monthlyPayment}
