mapper.select:
SELECT b.id AS boardId, b.title, b.content, u.name AS userName, f.id AS categoryId, f.name AS categoryName, (SELECT COUNT(*) FROM board_like WHERE board_id = b.id) AS likeCount, (SELECT COUNT(*) FROM comment WHERE post_id = b.id) AS commentCount, EXISTS ( SELECT 1 FROM board_scrap WHERE board_id = b.id AND user_id = #{userId} ) AS isScraped, EXISTS ( SELECT 1 FROM board_like WHERE board_id = b.id AND user_id = #{userId} ) AS isLiked, DATE_FORMAT(b.created_at, '%Y-%m-%d %H:%i:%s') AS createdAt FROM board b JOIN user_profile u ON b.user_id = u.user_id JOIN financial_tendency_type f ON b.financial_tendency_type_id = f.id WHERE b.financial_tendency_type_id = #{communityId} AND b.is_deleted = 0
AND b.id < #{lastBoardId}
ORDER BY b.id DESC LIMIT #{pageSize}

mapper.update:
UPDATE board
SET is_deleted = 1
WHERE id = #{boardId}
  AND financial_tendency_type_id = #{communityId}
  AND user_id = #{userId}

mapper.select:
SELECT is_deleted
FROM board_scrap
WHERE user_id = #{userId}
  AND board_id = #{boardId}
  AND financial_tendency_type_id = #{communityId}

mapper.update:
UPDATE board_scrap
SET is_deleted = #{isDeleted}
WHERE user_id = #{userId}
  AND board_id = #{boardId}
  AND financial_tendency_type_id = #{communityId}

mapper.select:
SELECT is_deleted
FROM board_like
WHERE user_id = #{userId}
  AND board_id = #{boardId}
  AND financial_tendency_type_id = #{communityId}

mapper.update:
UPDATE board_like
SET is_deleted = #{isDeleted}
WHERE user_id = #{userId}
  AND board_id = #{boardId}
  AND financial_tendency_type_id = #{communityId}
