mapper.update:
UPDATE comment
SET is_deleted = 1
WHERE id = #{commentId}
  AND parent_comment_id = #{boardId}
  AND user_id = #{userId}

mapper.select:
SELECT *
FROM comment
WHERE id = #{commentId}
  AND board_id = #{boardId}
  AND financial_tendency_type_id = #{communityId}
  AND is_deleted = 0

SELECT is_deleted
FROM comment_like
WHERE user_id = #{userId}
  AND board_id = #{boardId}
  AND comment_id = #{commentId}
  AND financial_tendency_type_id = #{communityId}

mapper.update:
UPDATE comment_like
SET is_deleted = #{isDeleted}
WHERE user_id = #{userId}
  AND board_id = #{boardId}
  AND comment_id = #{commentId}
  AND financial_tendency_type_id = #{communityId}
