mapper.update:
UPDATE User_Profile
SET name = #{name},
    password_hash = #{passwordHash}
WHERE user_id = #{userId}

UPDATE User_Info
SET selected_wonnabe_ids = #{selectedWonnabeIds}
WHERE user_id = #{userId}

mapper.select:
SELECT name
FROM Financial_Tendency_Type
WHERE id = #{nowmeId}

SELECT COUNT(*)
FROM User_Info
WHERE user_id = #{userId}
